import React, {useEffect, useState} from 'react'
import { View, ScrollView, TextInput, StyleSheet, Button, ActivityIndicator, Alert, Text, TouchableOpacity } from "react-native";
import  firebase  from "../../database/firebase";

const DetailUserScreen = (props) => {

    const initialState = {
        id: '',
        name: '',
        email: '',
        phone: ''
    }

    const [user, setUser] = useState(initialState);

    const [loading, setLoading] = useState(true)

    const getUserById = async (id) => {
       const dbRef = firebase.db.collection('users').doc(id)
       const doc = await dbRef.get();
       const user = doc.data();
       setUser ({
           ...user,
           id: doc.id
       });
       setLoading(false)
    }

    useEffect(() => {
        getUserById(props.route.params.userId);
    },[])

    const handleChangeText = (name, value) => {
        setUser({ ...user, [name]: value })
    }

    const deleteUser = async() => {
        const dbRef = firebase.db.collection('users').doc(props.route.params.userId);
        await dbRef.delete();
        props.navigation.navigate('ListUsers');
    }

    const confirmationDelete = () => {
        Alert.alert('Eliminar usuario', 'Esta seguro?', [
            {text: 'Si', onPress: () => deleteUser()},
            {text: 'No', onPress:() => console.log(false)},
        ]);
    }

    const updateUser = async () => {
        const dbRef = firebase.db.collection('users').doc(user.id);
        await dbRef.set({
            name: user.name,
            email: user.email,
            phone: user.phone
        });
        setUser(initialState);
        props.navigation.navigate('ListUsers');
    }

    if (loading) {
        return (
            <View>
                <ActivityIndicator size="large" color="#9e9e9e" />
            </View>
        )
    }

    return (
        <ScrollView style={styles.container}>
            <View style={styles.inputGroup}>
                <TextInput
                    value={user.name}  
                    placeholder="Nombre de usuario"
                    onChangeText={(value) => handleChangeText('name', value) }
                />
            </View>
            <View style={styles.inputGroup}>
                <TextInput 
                    value={user.email}
                    placeholder="Correo del usuario"
                    onChangeText={(value) => handleChangeText('email', value) }
                />
            </View>
            <View style={styles.inputGroup}>
                <TextInput 
                    value={user.phone}
                    placeholder="TelÃ©fono de usuario"
                    onChangeText={(value) => handleChangeText('phone', value) }
                />
            </View>
            <View style={styles.inputGroup}>
            <TouchableOpacity
                onPress={() => alert('Hello, world!')}
                style={{ backgroundColor: 'blue' }}>
                <Text style={{ fontSize: 20, color: '#fff' }}>Pick a photo</Text>
            </TouchableOpacity>
            </View>
            <View>
                <Button
                    color="#19AC52"
                    title="Actualizar usuario" 
                    onPress={()=> updateUser() } 
                />
            </View>                        
            <View>
                <Button
                    color="#E37399"
                    title="Eliminar usuario" 
                    onPress={()=> confirmationDelete() }
                />    
            </View>
        </ScrollView>
    )
}

const styles = StyleSheet.create({
    container:{
        flex:1,
        padding:35
    },
    inputGroup: {
        flex: 1,
        padding:0,
        marginBottom:15,
        borderBottomWidth: 1,
        borderBottomColor: '#cccccc'
    },
    button: {
        backgroundColor: "blue",
        padding: 20,
        borderRadius: 5,
      },
    buttonText: {
    fontSize: 20,
    color: '#fff',
    },     
})

export default DetailUserScreen
